import React from "react";
import Question from '../../Question';
import styled, { css } from 'styled-components/macro'
import Start from './components/Start'
import Quiz from './components/Quiz'
// reactstrap components


// core components
import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import TestPageHeader from "components/Headers/TestPageHeader.js";
import DefaultFooter from "components/Footers/DefaultFooter.js";

const Intro = styled.div`
  margin-top: 8em;
  text-align: center;
`;
const QuizWindow = styled.div`
    text-align: center;
    font-size: clamp(20px, 2.5vw, 24px);
    margin-top: 10vh;
`;
const Option = styled.button`
    display: block;
    border: 1px solid #616A94;
    border-radius: 15px;
    padding: 15px 30px;
    text-decoration: none;
    color: #616A94;
    background-color: #161A31;
    transition: 0.3s;
    font-size: 1em;
    outline: none;
    user-select: none;
    margin-top: 1em;
    cursor: pointer;
    
    @media screen and (min-width: 1180px) {
        &:hover {
            color: white;
            background-color: #616A94;
        }
    }
`;
function TestPage() {
  React.useEffect(() => {
    document.body.classList.add("test-page");
    document.body.classList.add("sidebar-collapse");
    document.documentElement.classList.remove("nav-open");
    window.scrollTo(0, 0);
    document.body.scrollTop = 0;
    return function cleanup() {
      document.body.classList.remove("test-page");
      document.body.classList.remove("sidebar-collapse");
    };
  }, []);
  const [start, setStart] = useState(false);
let numbers=1;
  const items = Question.map(data =>{
    if(data.num== numbers){
    return(
    <div>
      <ul>
        <li key={data.num}style={{position:'relative',left:'10vh'}}>
          <span>{data.num}</span>
          <span >{data.question}</span><br/>
          <Option >{data.answer1}</Option>
          <Option >{data.answer2}</Option>
        </li>
      </ul>
    </div>
    )
    }
  })
  
  return (
    <>
      <ExamplesNavbar />
      <QuizWindow>
      <div className="wrapper">
        <TestPageHeader />
       
         <div className="section section-team text-center">
          

         <div className="quiz">
      { start ? <Quiz /> : <Start props={setStart} />} 
    </div>
         {/* <Intro>
            <h1>Take the quiz.</h1>
            <h4>Whenever, you want.</h4>
            <button  css={btnCSS}>Begin</button>
        </Intro> */}
    



        </div>
      
        <DefaultFooter />
      </div>
      </QuizWindow>
    </>
  );
}

export default TestPage;
