import React from "react";

// reactstrap components
import {
  Container,
  Row,
  Col,
  Carousel,
  CarouselItem,
  CarouselIndicators,
  NavItem,
  NavLink,
  UncontrolledTooltip,
} from "reactstrap";

// core components

const items = [
  {
    src: require("assets/img/bg1.jpg").default,
    altText: "Nature",
    caption: "Nature",
  },
  {
    src: require("assets/img/bg3.jpg").default,
    altText: "Sunset",
    caption: "Sunset",
  },
  {
    src: require("assets/img/test.jpg").default,
    altText: "Cafe View",
    caption: "Cafe View",
    
  },
  {
    src: require("assets/img/bg5.jpg").default,
    altText: "Night View",
    caption: "Night View",
  },
  {
    src: require("assets/img/login.jpg").default,
    altText: "Sunset",
    caption: "Sunset",
  },
  {
    src: require("assets/img/bg4.jpg").default,
    altText: "Korea River",
    caption: "Korea River",
  },
  {
    src: require("assets/img/header.jpg").default,
    altText: "Korea View",
    caption: "Korea View",
  }
];

function CarouselSection() {
  const [activeIndex, setActiveIndex] = React.useState(0);
  const [animating, setAnimating] = React.useState(false);
  const onExiting = () => {
    setAnimating(true);
  };
  const onExited = () => {
    setAnimating(false);
  };
  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  };
  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  };
  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  };
  return (
    <>
      <div className="section" id="carousel">
        <Container>
        <NavItem style={{ backgroundColor: "black", textAlign:"center", height: "70px"}}>
                <NavLink
                  href="https://www.instagram.com/travel16_2monthsago"
                  target="_blank"
                  id="instagram-tooltip"
                >
                  <i className="fab fa-instagram"></i>
                  <p className="d-lg-none d-xl-none"></p>
                </NavLink>
                <UncontrolledTooltip target="#instagram-tooltip">
                  Follow us on Instagram
                </UncontrolledTooltip>
              </NavItem>
          <Row className="justify-content-center">
            <Col lg="8" md="12">
              <Carousel
                activeIndex={activeIndex}
                next={next}
                previous={previous}
              >
                <CarouselIndicators
                  items={items}
                  activeIndex={activeIndex}
                  onClickHandler={goToIndex}
                />
                {items.map((item) => {
                  return (
                    <CarouselItem
                      onExiting={onExiting}
                      onExited={onExited}
                      key={item.src}
                    >
                      <img src={item.src} alt={item.altText} />
                      <div className="carousel-caption d-none d-md-block">
                        <h5>{item.caption}</h5>
                      </div>
                    </CarouselItem>
                  );
                })}
                <a
                  className="carousel-control-prev"
                  data-slide="prev"
                  href="#pablo"
                  onClick={(e) => {
                    e.preventDefault();
                    previous();
                  }}
                  role="button"
                >
                  <i className="now-ui-icons arrows-1_minimal-left"></i>
                </a>
                <a
                  className="carousel-control-next"
                  data-slide="next"
                  href="#pablo"
                  onClick={(e) => {
                    e.preventDefault();
                    next();
                  }}
                  role="button"
                >
                  <i className="now-ui-icons arrows-1_minimal-right"></i>
                </a>
                
              </Carousel>
            </Col>
          </Row>
        </Container>
        <h5 style={{color:"white", display: "flex", justifyContent: "center"}}>instagram에서 더 많은 사진을 감상 하실 수 있습니다.</h5>
      </div></>
  );
}

export default CarouselSection;
