import React, { useState } from "react";
import axios from 'axios';
import YouTube from "react-youtube";
//import resizeLayout from "Components/Layout/ResizeLayout";
//import "./VideoModal.scss";

// reactstrap components
import { Button, Container, Row, Col } from "reactstrap";

// core components
import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import DarkFooter from "components/Footers/DarkFooter.js";

function MusicPage(props){
  const [names,setNames]=useState([]);
    const { onClose, isMobile } = props;
    const [open, setOpen] = useState(false);
    const handleModal = (flag) => {
      setOpen(flag);
      onClose();
    };

    <YouTube
    videoId={string}                  // defaults -> null
    id={string}                       // defaults -> null
    className={string}                // defaults -> null
    containerClassName={string}       // defaults -> ''
    opts={obj}                        // defaults -> {}
    onReady={func}                    // defaults -> noop
    onPlay={func}                     // defaults -> noop
    onPause={func}                    // defaults -> noop
    onEnd={func}                      // defaults -> noop
    onError={func}                    // defaults -> noop
    onStateChange={func}              // defaults -> noop
    onPlaybackRateChange={func}       // defaults -> noop
    onPlaybackQualityChange={func}    // defaults -> noop
  />
  
    const opts = {
      width: "640",
      height: "390",
      playerVars: {
        autoplay: 1,
      },
    };
  
    const mobileOpts = {
      width: "320px",
      height: "200px",
      playerVars: {
        autoplay: 1,
      },
    };
    
  React.useEffect(() => {
    document.body.classList.add("music-page");
    document.body.classList.add("sidebar-collapse");
    document.documentElement.classList.remove("nav-open");
    window.scrollTo(0, 0);
    document.body.scrollTop = 0;
    return function cleanup() {
      document.body.classList.remove("music-page");
      document.body.classList.remove("sidebar-collapse");
    };
  }, []);

  const Namelists= names.map((name)=> {
    const searchurl="https://search.daum.net/search??w=tot&&q="+ name.facltNm
    const mapurl="https://map.kakao.com/link/search/"+name.addr1
    return(
      <div className="section3 section-nucleo-icons">

      <Container>
          <Row>
            <Col lg="6" md="12">
              <h2 className="title">{name.facltNm}</h2>
             <h5 className="description">
             &nbsp;{name.doNm}&nbsp;{name.induty}
              </h5>
              <Button
                className="btn-detail"
                color="info"
                size="lg"
                href = {searchurl}
                target="_blank"
              > 자세히
              </Button>
              <Button
                className="btn-detail"
                color="info"
                href={mapurl}
                size="lg"
                target="_blank"
              >
                길찾기
              </Button>
            </Col>
          </Row>
        </Container>
      </div>
   );
  }
);

  return (
    <><>
      <ExamplesNavbar />
      <div className="page-header clear-filter">
        <div
          className="page-header-image"
          style={{
            backgroundImage: "url(" + require("assets/img/login1.jpg").default + ")",
          }}
        ></div>
      </div>

      </><div class="zone_wrap">
        <div class="list_zone">
          <div class="tit">
            <h2 id="zoneTitle">음악여행</h2>
          </div>

          <div
      className="video-modal-container"
      onClick={() => handleModal(false)}
      style={{ position: "fixed" }}
    >
      <div className="video-modal-wrapper">
        <span className="video-white-x" onClick={() => handleModal(false)}>
          x
        </span>

        {isMobile ? (
          <YouTube videoId={props.jh_71Ig00js} opts={mobileOpts} />
        ) : (
          <YouTube videoId={props.videoID} opts={opts} />
          )}
        </div>
      </div>
        </div>
      </div>     
      <DarkFooter /></>
  );
}

export default MusicPage;
