import React ,{useState}from "react"
import axios from "axios";
import {Container, Button, Input } from "reactstrap";
import moment from 'moment';
// 안써도 자동으로 한국 시간을 불러온다. 명확하게 하기 위해 import
import 'moment/locale/ko';
import { useParams } from "react-router-dom";
function BoardRead() {
    const {num} = useParams(); 
    
    const [Board, setBoard] = useState({
    idx: "",
    title: "",
    content: "",
    board_date: "",
    id: "",
    views: ""
}
     );
     //let pageHeader = React.createRef();


     React.useEffect(() => {
        axios.get('http://localhost:8080/api/board/'+num)
        .then(function (response) {
            setBoard(response.data);
      
        })
        .catch(function (error) {
          console.log(error);
        });
   
        // document.body.classList.add("boardread/:num");
        // document.body.classList.add("sidebar-collapse");
        // document.documentElement.classList.remove("nav-open");
        // window.scrollTo(0, 0);
        // document.body.scrollTop = 0;
        // return function cleanup() {
        //   document.body.classList.remove("boardread/:num");
        //   document.body.classList.remove("sidebar-collapse");
        // };
      }, []);

     const f3 = async () => {
        console.log(Board);
        
      }

      //alert(Board.title);
    return(
        <div>
<div className="page-header">
        <div
          className="page-header-image"
          style={{
            backgroundImage:
              "url(" + require("assets/img/bg4.jpg").default + ")",
          }}
          //ref={pageHeader}
        ></div>
        <div className="content-center">
          <Container>
            <h1 className="title">ㄱㅔㅅㅣㅍㅏㄴ</h1><br/>
            <h2><Input
            placeholder={Board.title}
            name="title"  disabled
            ></Input></h2>
            
            <h2><Input
       
            placeholder={Board.content}
            type="textarea"
            name="content" disabled
            /></h2>
            <br/>
            <Button
            block
            className="btn-view"
            color="black"
            href="../board-page"
            size="5px"
            >
                목록
                </Button>
                <Button
            block
            className="btn-board"
            color="black"
            // href=""
            onClick={f4}
            size="5px"
            >
                삭제
                </Button>
            <Button
            block
            className="btn-board"
            color="black"
            // href=""
            onClick={f3}
            size="5px"
            >
                수정
                </Button>
                
          </Container>
        </div>
      </div>
      </div>
    )
}

export default BoardRead;
