import React ,{useState}from "react"
import axios from "axios";
import {Container, Button, Input } from "reactstrap";
import moment from 'moment';
// 안써도 자동으로 한국 시간을 불러온다. 명확하게 하기 위해 import
import 'moment/locale/ko';
import { useParams } from "react-router-dom";
function BoardRead() {
    const {num} = useParams(); 
    
    const [Board, setBoard] = useState({
    idx: "",
    title: "",
    content: "",
    board_date: "",
    id: "",
    views: ""
}
     );
     const [Comment, setComment] = useState({
      idx: "",
      title: "",
      content: "",
      comment_date: "",
      board_id: "",
      comment_id: "",
      board_no: "",
      views: ""
  }
       );
     //let pageHeader = React.createRef();


     React.useEffect(() => {
        axios.get('http://localhost:8080/api/board/'+num)
        .then(function (response) {
            setBoard(response.data);
      
        })
        .catch(function (error) {
          console.log(error);
        });
        axios.get('http://localhost:8080/api/getCommentById/'+num)
        .then(function (response) {
          setComment(response.data)
      
        })
        .catch(function (error) {
          console.log(error);
        });
   
        // document.body.classList.add("boardread/:num");
        // document.body.classList.add("sidebar-collapse");
        // document.documentElement.classList.remove("nav-open");
        // window.scrollTo(0, 0);
        // document.body.scrollTop = 0;
        // return function cleanup() {
        //   document.body.classList.remove("boardread/:num");
        //   document.body.classList.remove("sidebar-collapse");
        // };
      }, []);

     const f3 = async () => {
        
        window.location.href ='http://localhost:3000/boardinput/'+Board.idx
      }
      const f4 = async () => {
        console.log(Board);
        //삭제/board/{no}
        axios.delete('http://localhost:8080/api/board/'+num)
        .then(function (response) {
          window.location.href ='http://localhost:3000/board-page'
        })
        .catch(function (error) {
          console.log(error);
        });
   
      }


     const comment= ()=> {
        if (this.state.board.category != '홍보게시판' && this.state.board.category != '공지사항') {
            return (
                <div>
                    <div className="card col-md-10 offset-md-1" >

                        <div className="row" >

                            <textarea className="comment-textarea"
                                type="text"
                                placeholder="댓글" name="answer"
                                value={this.state.answer}
                                onChange={this.changeanswer}
                            />
                            <button className="main-btn" onClick={this.createComment} >댓글</button>


                        </div>
                    </div>
                    {
                        comments.map(
                            comment =>
                                <div key={comment} className="card col-md-10 offset-md-1">
                                    <div className="row"  >
                                        &nbsp;&nbsp;&nbsp;&nbsp; <h5>{comment.comment_id}</h5> &nbsp;  &nbsp; &nbsp; {this.returnDate(comment.comment_date)}
                                        <br />
                                        <div style={{ position: "absolute", top: "0px", right: "5%" }}>
                                            <a onClick={() => this.updateComment(comment.comment_date, comment.comment_id, comment.answer, comment.comment_like, comment.comment_no, this.state.id,)}><img src={heart} style={{ width: "20px", height: "20px", marginBottom: "3px" }} alt='heart' ></img>{comment.comment_like}</a>

                                        &nbsp;&nbsp;&nbsp;
                                        {this.checkidcomment(comment.comment_id, comment.comment_no)}
                                        </div>
                                    </div>
                                    {comment.answer}

                                    {comment.comement_id}<br />
                                </div>
                        )
                    }
                </div>
            )
        }
    }

      //alert(Board.title);
    return(
        <div>
<div className="page-header">
        <div
          className="page-header-image"
          style={{
            backgroundImage:
              "url(" + require("assets/img/bg4.jpg").default + ")",
          }}
          //ref={pageHeader}
        ></div>
        <div className="content-center">
          <Container>
            <h1 className="title">ㄱㅔㅅㅣㅍㅏㄴ</h1><br/>
            <h2><Input
            placeholder={Board.title}
            name="title"  disabled
            ></Input></h2>
            
            <h2><Input
       
            placeholder={Board.content}
            type="textarea"
            name="content" disabled
            /></h2>
            <br/>
            <Button
            block
            className="btn-view"
            color="black"
            href="../board-page"
            size="5px"
            >
                목록
                </Button>
                <Button
            block
            className="btn-board"
            color="black"
            // href=""
            onClick={f4}
            size="5px"
            >
                삭제
                </Button>
            <Button
            block
            className="btn-board"
            color="black"
            // href=""
            onClick={f3}
            size="5px"
            >
                수정
                </Button>
              
          </Container>
        </div>
      </div>
      </div>
    )
}

export default BoardRead;
