import React, { useState } from "react";
import axios from 'axios';

// reactstrap components
import { Button, Container, Row, Col } from "reactstrap";

// core components
import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import DarkFooter from "components/Footers/DarkFooter.js";

function CampingPage() {
  const [names,setNames]=useState([]);

  const [keyword,setKeyword]=useState();
  const [pagingData,setData]=useState([]);
  React.useEffect(() => {
    let isSubscribed = true;
    axios
    .get(
      '/api/getcamping'
    )
    .then(Response => {
      if (Response.status === 200) {
            
        setNames(Response.data.response.body.items.item);
        console.log(Response.data.response.body.items.item);
        setData(Response.data.pagingData);
       // check if this component still mounted
       if (isSubscribed) {
         //setLoading(false);
       }
     }
    });
    //.catch((Error)=>{console.log(Error)});
      
    document.body.classList.add("camping-page");
    document.body.classList.add("sidebar-collapse");
    document.documentElement.classList.remove("nav-open");
    window.scrollTo(0, 0);
    document.body.scrollTop = 0;
    return function cleanup() {
      document.body.classList.remove("camping-page");
      document.body.classList.remove("sidebar-collapse");
    };
  }, []);

  const Namelists= names.map((name)=> {
           const searchurl="https://search.daum.net/search??w=tot&&q="+ name.facltNm
           const mapurl="https://map.kakao.com/link/search/"+name.addr1
        return(
          <div className="section3 section-nucleo-icons">
    
          <Container>
              <Row>
                <Col lg="6" md="12">
                  <h2 className="title">{name.facltNm}</h2>
                 <h5 className="description">
                 &nbsp;{name.doNm}&nbsp;{name.induty}
                  </h5>
                  <Button
                    className="btn-detail"
                    color="info"
                    size="lg"
                    href = {searchurl}
                    target="_blank"
                  > 자세히
                  </Button>
                  <Button
                    className="btn-detail"
                    color="info"
                    href={mapurl}
                    size="lg"
                    target="_blank"
                  >
                    길찾기
                  </Button>
                </Col>
              </Row>
            </Container>
          </div>
       );
      }
);

function listBoard(num, keyword)
{

    axios.get('/api/getPagingBoard?p_num='+num+'&keyword='+keyword)
    .then(Response => {
      console.log(Response.data.pagingData)
        if (Response.status === 200) {
          setData(Response.data.pagingData);
          setNames(Response.data.response.body.items.item);
          
       }
    })
  };

function viewPaging() {
  const pageNums = [];
  for (let i = pagingData.pageNumStart; i <= pagingData.pageNumEnd; i++) {
      pageNums.push(i);
  }
  let currentpage = pagingData.currentPageNum;
  return (pageNums.map((page) =>
      <li className="page-item" key={page.toString()}>
          <a className="page-link" onClick={() => listBoard(page , keyword)}>
              {
                  (function () {
                      if (page == currentpage)
                          return (<div style={{ color: '#ff5d28', fontWeight: 'bold' }}>{page}</div>);
                      else return (<div>{page}</div>);
                  })()
              }
          </a>
      </li>
  ));
};

function isPagingPrev() {
  if (pagingData.prev) {
      return (
          <li className="page-item">
              <a className="page-link" onClick={() => listBoard(pagingData.currentPageNum - 1,keyword)} tabIndex="-1">Previous</a>
          </li>
      );
  }
};

function isPagingNext() {
  if (pagingData.next) {
      return (
         <li className="page-item">
              <a className="page-link" onClick={() => listBoard(pagingData.currentPageNum + 1, keyword)} tabIndex="-1">Next</a>
          </li>
      );
  }
};

  return (
    <><>
      <ExamplesNavbar />
      <div className="page-header clear-filter">
        <div
          className="page-header-image"
          style={{
            backgroundImage: "url(" + require("assets/img/login1.jpg").default + ")",
          }}
        ></div>
            <div className="content-center">
          <Container>
            <h1 className="title">Camping Trip</h1>
          </Container>
        </div>
      </div>



      </><div class="zone_wrap">
        <div class="list_zone">
          <div class="tit">
          </div>
          {Namelists}
          <input type="text"
          placeholder="검색하기.."
          name="search"
          type="text"
          className="text-search"
          onChange={e => setKeyword(e.target.value)} />
          
          <Button className="btn-search"
                color="info"
                onClick={() => listBoard(1, keyword)}
                //target="_blank"
                >조회</Button>
      </div>
          { <div >                   
                            <nav aria-label="Page navigation example">
                                <ul className="pagination justify-content-center">
                               
                                    {
                                        isPagingPrev()
                                    }
                                    {
                                        viewPaging()
                                    }
                                    {
                                        isPagingNext()
                                    }
                                </ul>
                            </nav>
                        </div>
      }
      <DarkFooter />
      </div></>
  );
}

export default CampingPage;
